  console.log('Session storage is empty');
}
```

**Utility Examples:**
```javascript
// Get all keys
function getAllKeys() {
  const keys = [];
  for (let i = 0; i < sessionStorage.length; i++) {
    keys.push(sessionStorage.key(i));
  }
  return keys;
}

// Get all items
function getAllItems() {
  const items = {};
  for (let i = 0; i < sessionStorage.length; i++) {
